import re

for record in records:
  try:
    for field in re.finditer(r"(\b\w+=.*?(?=\s\w+=|$))", record.value['remaining']):
      split_field = field.group(0).split("=")
      record.value[split_field[0]] = split_field[1][1:-1]
    output.write(record)

  except Exception as e:
    # Send record to error
    error.write(record, str(e))
